# Baixa cada imagem e salva com nome image1.jpg, image2.jpg, ...
$i = 1
foreach ($url in $urls) {
    $outputFile = Join-Path $destFolder ("image$i.jpg")
    Write-Host "Baixando imagem $i..."
    Invoke-WebRequest -Uri $url -OutFile $outputFile -MaximumRedirection 10
    $i++
}
